    private static Integer letsDoIt() {
        int floor = 0;
        int positionToGetInBasement = -1;
        for (int position = 0; position < puzzleInput.length(); position++) {
            if (Character.compare(puzzleInput.charAt(position), '(') == 0) {
                floor++;
            }
            if (Character.compare(puzzleInput.charAt(position), ')') == 0) {
                floor--;
            }
            if (floor == -1 && positionToGetInBasement == -1) {
                positionToGetInBasement = position + 1;
            }
        }
        System.out.println("Santa get in the basement on position " + positionToGetInBasement);
        return floor;
    }

